import React, { useState } from "react";

const FileUpload = ({
    label,
    accept,
    icon,
    entityType,
    entityId,
    preview = false,
    onUpload,
    helperText,
}) => {
    const [file, setFile] = useState(null);
    const [showPreview, setShowPreview] = useState(false);

    const handleChange = (e) => {
        const selectedFile = e.target.files[0];
        setFile(selectedFile);
        if (onUpload && selectedFile) {
            onUpload(selectedFile, { entityType, entityId });
        }
    };

    return (
        <div className="file-upload">
        <label>{label}</label>
        <input type="file" accept={accept} onChange={handleChange} />
        {file && (
            <div className="file-info">
            <span onClick={() => preview && setShowPreview(true)} style={{ cursor: preview ? "pointer" : "default" }}>
            {icon}
            </span>
            <span>{file.name}</span>
            </div>
        )}
        {helperText && <div className="helper-text">{helperText}</div>}
        {/* Модальное окно предпросмотра PDF */}
        {showPreview && preview && (
            <div className="modal" onClick={() => setShowPreview(false)}>
            <div className="modal-content" onClick={e => e.stopPropagation()}>
            <button onClick={() => setShowPreview(false)}>Закрыть</button>
            <iframe src={URL.createObjectURL(file)} width="100%" height="600px" title="PDF Preview" />
            </div>
            </div>
        )}
        </div>
    );
};

export default FileUpload;
