import React, { useState, useEffect } from "react";
import "../css/Water.css";

import { fetchStructDataWithFilters } from "../api/fetch_records";
import { sendFormData } from "../api/add_records";

const Water = () => {
  const orgData = localStorage.getItem("org");
  let orgInfo = {};

  if (orgData) {
    try {
      orgInfo = JSON.parse(orgData);
    } catch (error) {
      console.error("Ошибка парсинга org:", error);
      orgInfo = {};
    }
  }

  const waterPoints = {
    "Пункт 1": "54°20′0″N 37°30′0″E",
    "Пункт 2": "55°45′0″N 38°10′0″E"
  };

  const [Orgs, setObjects] = useState([]);
  const [Points, setObjectsPoints] = useState([]);
  // const [Meters, setMeters] = useState([]);

  useEffect(() => {
    const loadData = async () => {
      try {
        const resp = await fetchStructDataWithFilters(
          "organisations_familiar",
          { org_id: orgInfo.id }
        );
        console.log(resp);
        const [orgs, points] = await Promise.all([
          resp?.data.orgs,
          resp?.data.points
          // fetchSingleTableData("meters")
        ]);
        setObjects(orgs || []);
        setObjectsPoints(points || []);
        // setMeters(meters || [])
      } catch (error) {
        console.error("Ошибка загрузки данных", error);
        setObjects([]);
        setObjectsPoints([]);
        // setMeters([])
      }
    };
    loadData();
  }, []);

  const [formData, setFormData] = useState({
    organisationName: orgInfo.organisation_name || "",
    waterOrg: "",
    controlPoint: "",
    coordinates: "",
    deviceNumber: "", // Используем deviceNumber
    waterSource: "",
    measurementDate: "",
    workingTime: "",
    waterUsage: ""
  });

  const [completedSteps, setCompletedSteps] = useState({
    section1_2: false,
    section4: false
  });

  const [activeSection, setActiveSection] = useState(1);
  const [manualNavigation, setManualNavigation] = useState(false);

  const [selectedPoint, setSelectedPoint] = useState(null);
  const handleChange = (e) => {
    const { name, value } = e.target;
    let updatedFormData = { ...formData, [name]: value };

    if (name === "controlPoint") {
      const selected = Points.find(
        (point) => point.latitude_longitude === value
      );
      setSelectedPoint(selected);
      updatedFormData.latitude_longitude = value;
      updatedFormData.coordinates = waterPoints[value] || "";
      // Обновляем deviceNumber при выборе controlPoint
      updatedFormData.deviceNumber =
      selected?.meter_id?.brand?.brand_name &&
      selected?.meter_id?.serial_number
      ? `${selected.meter_id.brand.brand_name} - ${selected.meter_id.serial_number}`
      : "";
    }

    setFormData(updatedFormData);
    checkCompletion(updatedFormData);
  };

  const checkCompletion = (data) => {
    let newCompletedSteps = { ...completedSteps };

    newCompletedSteps.section1_2 =
    data.waterOrg.trim() !== "" && data.controlPoint.trim() !== "";
    newCompletedSteps.section4 =
    data.measurementDate.trim() !== "" &&
    data.workingTime.trim() !== "" &&
    data.waterUsage.trim() !== "" &&
    data.deviceNumber.trim() !== ""; // Проверяем deviceNumber

    setCompletedSteps(newCompletedSteps);

    if (!manualNavigation) {
      if (newCompletedSteps.section1_2 && activeSection === 1)
        setActiveSection(2);
    }
  };

  const handleStepClick = (step) => {
    setActiveSection(step);
    setManualNavigation(true);
  };

  const handleSubmit = async () => {
    if (!formData.deviceNumber) {
      // Проверяем deviceNumber
      alert("Выберите прибор учета!");
      return;
    }

    const data = {
      measurement_date: formData.measurementDate,
      operating_time_days: formData.workingTime,
      water_consumption_m3_per_day: formData.waterUsage,
      meter_readings: formData.deviceNumber, // Передаем deviceNumber
      water_point_id: formData.controlPoint // ID пункта учета воды
    };

    console.log("Отправка данных:", data);

    try {
      const response = await sendFormData("water_consumption_single", data);
      alert("Данные успешно отправлены!");
      console.log("ttt", response);
      setFormData({
        organisationName: orgInfo.organisation_name || "",
        waterOrg: "",
        controlPoint: "",
        coordinates: "",
        deviceNumber: "", // Используем deviceNumber
        waterSource: "",
        measurementDate: "",
        workingTime: "",
        waterUsage: ""
      });
    } catch (error) {
      console.error("Ошибка отправки данных:", error);
      alert("Ошибка при отправке данных. Проверьте соединение.");
    }
  };

  return (
    <div className="water-container">
    <div className="steps">
    {[1, 2].map((step) => (
      <div
      key={step}
      className={`step ${
        completedSteps[step === 1 ? "section1_2" : "section4"]
        ? "completed"
        : ""
      } ${activeSection === step ? "active" : ""}`}
      onClick={() => handleStepClick(step)}
      >
      {step}
      </div>
    ))}
    </div>

    <div className="form-container">
    {activeSection === 1 && (
      <div className="form-step">
      <h2>Журнал учета водопотребления</h2>
      {/* Объединяем поля из секций 1 и 2 */}
      <div className="input-group">
      <label>
      Наименование организации: {formData.organisationName || "Без организации"}
        </label>
        </div>
        <div className="input-group">
        <label>
        Наименование организации (забор воды):
        <select
        name="waterOrg"
        value={formData.waterOrg}
        onChange={handleChange}
        >
        <option value="">Выбрать организацию</option>
        {Orgs.map((obj) => (
          <option
          key={obj.organization_code.code_symbol}
          value={obj.organisation_name}
          >
          {obj.organization_code.code_value} -{" "}
          {obj.organisation_name}
          </option>
        ))}
        </select>
        </label>
        </div>

        <div className="input-group">
        <label>
        Наименование пункта учета:
        <select
        name="controlPoint"
        value={formData.controlPoint}
        onChange={handleChange}
        >
        <option value="">Выбрать пункт учета</option>
        {Points.map((obj) => (
          <option
          key={obj.id}
          value={obj.latitude_longitude}
          >
          {obj.water_body_id.code_obj.code_symbol} -{" "}
          {obj.latitude_longitude} ({obj.point_type})
          </option>
        ))}
        </select>
        </label>
        </div>
        <div className="input-group">
        <label>Координаты пункта: {formData.latitude_longitude}</label>
        </div>
        </div>
    )}

    {activeSection === 2 && (
      <div className="form-step">
