import React, { useState, useEffect } from "react";
import "../css/Water.css";
import {
  fetchSingleTableData,
  fetchSingleTableDataWithFilters,
  fetchStructDataWithFilters,
} from "../api/fetch_records";
import { translate } from "../utils/translations.js";
import { uploadFile } from "../api/add_files";
import { getFiles, downloadFile } from "../api/fetch_files";

const AccountingPost = () => {
  const [filteredLogs, setFilteredLogs] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [allLogs, setAllLogs] = useState([]);
  const [monthFilter, setMonthFilter] = useState(new Date().getMonth());
  const [yearFilter, setYearFilter] = useState(new Date().getFullYear());
  const [statusFilters, setStatusFilters] = useState({
    in_progress: true,
    is_done: true,
    sent: true,
    under_watch: true,
    under_correction: true,
    closed: true,
  });
  const [expandedLogs, setExpandedLogs] = useState({});
  const [logDetails, setLogDetails] = useState({});

  const [pdfFile, setPdfFile] = useState(null);
  const [sigFile, setSigFile] = useState(null);

  const [uploadedPdf, setUploadedPdf] = useState(null);
  const [uploadedSig, setUploadedSig] = useState(null);

  const userInfo = JSON.parse(localStorage.getItem("user"));
  const orgData = localStorage.getItem("org");
  let orgInfo = {};

  if (orgData) {
    try {
      orgInfo = JSON.parse(orgData);
    } catch (error) {
      console.error("Ошибка парсинга org:", error);
      orgInfo = {};
    }
  }

  useEffect(() => {
    const loadData = async () => {
      try {
        const response = await fetchStructDataWithFilters("logs_for_AP", {
          role: userInfo.role,
          org_id: orgInfo?.id,
        });

        if (response && response.data) {
          const enrichedLogs = response.data.map((log) => {
            const waterBody = log.point_id?.water_body_id;
            const org = log.point_id?.organisation_id;

            return {
              id: log.id,
              water_body_name:
              waterBody?.code_obj_id?.code_value || "Без названия",
              organisation_name: org?.organisation_name || "Неизвестно",
              coordinates: log.point_id?.latitude_longitude || "-",
              point_type: log.point_id?.point_type || "-",
              start_date: log.start_date || "-",
              status: log.log_status || "Неизвестно",
            };
          });
          setAllLogs(enrichedLogs);
          setFilteredLogs(enrichedLogs);
        } else {
          setAllLogs([]);
          setFilteredLogs([]);
        }
      } catch (error) {
        console.error("Ошибка загрузки данных", error);
        setError("Не удалось загрузить данные");
      } finally {
        setIsLoading(false);
      }
    };

    loadData();
  }, []);

  useEffect(() => {
    const applyFilters = () => {
      if (allLogs.length === 0) return;

      const filtered = allLogs.filter((log) => {
        const startDate = new Date(log.start_date);
        const logMonth = startDate.getMonth();
        const logYear = startDate.getFullYear();

        const dateFilter =
        (monthFilter === new Date().getMonth() || logMonth === monthFilter) &&
        (yearFilter === new Date().getFullYear() || logYear === yearFilter);

        const status = log.status.toLowerCase();
        const statusFilter = Object.keys(statusFilters).some((key) => {
          return statusFilters[key] && status.includes(key);
        });

        return dateFilter && statusFilter;
      });

      setFilteredLogs(filtered);
    };

    applyFilters();
  }, [monthFilter, yearFilter, statusFilters, allLogs]);

  const handleMonthChange = (event) => {
    setMonthFilter(parseInt(event.target.value));
  };

  const handleYearChange = (event) => {
    setYearFilter(parseInt(event.target.value));
  };

  const handleStatusChange = (event) => {
    const { name, checked } = event.target;
    setStatusFilters((prev) => ({ ...prev, [name]: checked }));
  };

  const fetchUploadedFiles = async (logId) => {
    try {
      const token = localStorage.getItem('jwtToken');
      const pdfInfo = await getFiles(
        "water_consumption_log",
        logId,
        "MONTH_CLOSURE_SCAN"
      );
      const sigInfo = await getFiles(
        "water_consumption_log",
        logId,
        "SIGNATURE"
      );

      if (pdfInfo && pdfInfo.file_url) {
        setUploadedPdf({
          file_url: `${pdfInfo.file_url}&tokenJWTAuthorization=${pdfInfo.token}`,
        });
      } else {
        setUploadedPdf(null);
      }

      if (sigInfo && sigInfo.file_url) {
        setUploadedSig({
          file_url: `${sigInfo.file_url}&tokenJWTAuthorization=${sigInfo.token}`,
        });
      } else {
        setUploadedSig(null);
      }
    } catch (error) {
      console.error("Ошибка при получении загруженных файлов:", error);
    }
  };

  const handleExpandLog = async (logId) => {
    if (expandedLogs[logId]) {
      setExpandedLogs((prev) => ({ ...prev, [logId]: false }));
      setLogDetails((prev) => ({ ...prev, [logId]: null }));
      setUploadedPdf(null);
      setUploadedSig(null);
    } else {
      try {
        const response = await fetchStructDataWithFilters("log_details", {
          log_id: logId,
        });
        if (response && response.data) {
          setLogDetails((prev) => ({ ...prev, [logId]: response.data }));
        } else {
          setLogDetails((prev) => ({ ...prev, [logId]: null }));
        }
        await fetchUploadedFiles(logId); // Загружаем файлы при открытии журнала
      } catch (error) {
        console.error("Ошибка загрузки деталей журнала", error);
        setLogDetails((prev) => ({ ...prev, [logId]: null }));
      }
      setExpandedLogs((prev) => ({ ...prev, [logId]: true }));
    }
  };

  const [statusDropdownOpen, setStatusDropdownOpen] = useState(false);

  const toggleStatusDropdown = () => {
    setStatusDropdownOpen((prev) => !prev);
  };

  const getStatusName = (key) => {
    const labels = {
      in_progress: "В процессе",
      is_done: "Подписан",
      sent: "Отправлен",
      under_watch: "На рассмотрении",
      under_correction: "На доработке",
      closed: "Закрыт",
    };
    return labels[key] || key;
  };

  const handlePdfChange = (event) => {
    setPdfFile(event.target.files[0]);
  };

  const handleSigChange = (event) => {
    setSigFile(event.target.files[0]);
  };

  const handleFileUpload = async (logId, logStatus) => {
    if (
      (logStatus !== "in_progress" &&
      logStatus !== "is_done") ||
      (logStatus !== "IN_PROGRESS" &&
      logStatus !== "IS_DONE")
    ) {
      alert("Изменение файлов запрещено для текущего статуса журнала.");
      return;
    }

    if (!pdfFile && !sigFile) {
      alert("Пожалуйста, выберите хотя бы один файл для загрузки.");
      return;
    }

    try {
      if (pdfFile) {
        const pdfResult = await uploadFile(
          pdfFile,
          "water_consumption_log",
          logId,
          "MONTH_CLOSURE_SCAN",
          "Скан PDF"
        );
        if (pdfResult.status !== 200) {
          console.error("Ошибка загрузки PDF:", pdfResult);
          alert(`Ошибка загрузки PDF: ${pdfResult.message}`);
          return;
        }
      }

      if (sigFile) {
        const sigResult = await uploadFile(
          sigFile,
          "water_consumption_log",
          logId,
          "SIGNATURE",
          "Файл подписи"
        );
        if (sigResult.status !== 200) {
          console.error("Ошибка загрузки SIG:", sigResult);
          alert(`Ошибка загрузки SIG: ${sigResult.message}`);
          return;
        }
      }

      alert("Файлы успешно загружены!");
      await fetchUploadedFiles(logId); // Обновляем отображение загруженных файлов
      setPdfFile(null);
      setSigFile(null);
    } catch (error) {
      console.error("Произошла ошибка при загрузке файлов:", error);
      alert("Произошла ошибка при загрузке файлов.");
    }
  };

  const handleRemoveUploadedFile = async (logId, fileType) => {
    //Тут надо будет добавить логику удаления
    if (fileType === "pdf") {
      setUploadedPdf(null);
    } else {
      setUploadedSig(null);
    }
  };
  const handleDownload = (fileUrl, filename, token) => {
    downloadFile(fileUrl, token, filename);
  };

  return (
    <div className="accounting-container">
    <h2>Журнал учета водопотребления</h2>

    {isLoading ? (
      <p>Загрузка данных...</p>
    ) : error ? (
      <p>{error}</p>
    ) : (
      <div>
      <div className="filters-container">
      <div className="filter-block">
      <label>Месяц</label>
      <select value={monthFilter} onChange={handleMonthChange}>
      {[...Array(12)].map((_, month) => (
        <option key={month} value={month}>
        {new Date(2024, month, 1).toLocaleString("default", {
          month: "long",
        })}
        </option>
      ))}
      </select>
      </div>

      <div className="filter-block">
      <label>Год</label>
      <select value={yearFilter} onChange={handleYearChange}>
      {[2020, 2021, 2022, 2023, 2024, 2025].map((year) => (
        <option key={year} value={year}>
        {year}
        </option>
      ))}
      </select>
      </div>

      <div className="filter-block">
      <label>Статус: </label>
      <button className="status-toggle" onClick={toggleStatusDropdown}>
      {statusDropdownOpen ? "Скрыть" : "Выбрать статусы"}
      </button>
      {statusDropdownOpen && (
        <div className="status-dropdown">
        {Object.keys(statusFilters).map((key) => (
          <label className="status-item" key={key}>
          <span className="status-label">{getStatusName(key)}</span>
          <input
          type="checkbox"
          name={key}
          checked={statusFilters[key]}
          onChange={handleStatusChange}
          />
          </label>
        ))}
        </div>
      )}
      </div>
      </div>
      {/* Таблица */}
      {filteredLogs.length === 0 ? (
        <p>Нет данных</p>
      ) : (
        <table className="accounting-table">
        <thead>
        <tr>
        {userInfo.role !== "UserRoles.EMPLOYEE" && (
          <th>Организация</th>
        )}
        <th>Название водного объекта</th>
        <th>Координаты</th>
        <th>Тип</th>
        <th>Дата открытия</th>
        <th>Статус</th>
        {userInfo.role === "UserRoles.EMPLOYEE" && (
          <th>Действия</th>
        )}
        </tr>
        </thead>
        <tbody>
        {filteredLogs.map((log) => (
          <tr key={log.id}>
          {userInfo.role !== "UserRoles.EMPLOYEE" && (
            <td>{log.organisation_name}</td>
          )}
          <td>{log.water_body_name}</td>
          <td>{log.coordinates}</td>
          <td>{translate(log.point_type)}</td>
          <td>{log.start_date}</td>
          <td>{translate(log.status)}</td>
          {userInfo.role === "UserRoles.EMPLOYEE" && (
            <td>
            <button onClick={() => handleExpandLog(log.id)}>
            {expandedLogs[log.id] ? "Скрыть журнал" : "Открыть журнал"}
            </button>
            </td>
          )}
          </tr>
        ))}
        </tbody>
        </table>
      )}

      {/* 📘 Третий блок — журнал */}
      {Object.entries(expandedLogs).map(([logId, isExpanded]) =>
        isExpanded && logDetails[logId] ? (
          <div key={logId} className="log-details-container">
          <h3>Детали журнала (ID: {logId})</h3>
          <p>
          <strong>Эксплуатирующая организация:</strong>{" "}
          {logDetails[logId].exploitation_org.organisation_name}
          </p>
          <table className="data-table">
          <thead>
          <tr>
          <th>Дата измерения</th>
          <th>Дней эксплуатации</th>
          <th>Расход воды (м³/день)</th>
          <th>Подпись лица</th>
          </tr>
          </thead>
          <tbody>
          {logDetails[logId].wcl_list.map((m) => (
            <tr key={m.measurement_date}>
            <td>{m.measurement_date}</td>
            <td>{m.operating_time_days}</td>
            <td>{m.water_consumption_m3_per_day}</td>
            <td>{m.person_signature}</td>
            </tr>
          ))}
          </tbody>
          </table>

          {/* Добавляем поля для загрузки файлов */}
          <div>
          <h4>Загрузка файлов</h4>
          <div>
          <label>PDF скан:</label>
          {!uploadedPdf ? (
            <>
            <input
            type="file"
            accept=".pdf"
            onChange={handlePdfChange}
            />
            </>
          ) : (
            <>
            {uploadedPdf.file_url && (
              <object
              data={uploadedPdf.file_url}
              type="application/pdf"
              width="600"
              height="400"
              >
              Ваш браузер не поддерживает отображение PDF.
              <a href={uploadedPdf.file_url}>Скачать PDF</a>.
              </object>
            )}
            <button
            onClick={() =>
              handleRemoveUploadedFile(logId, "pdf")
            }
            disabled={
              logDetails[logId].log_status !== "in_progress" &&
              logDetails[logId].log_status !== "is_done"||
              logDetails[logId].log_status !== "IN_PROGRESS"  &&
              logDetails[logId].log_status !== "IS_DONE"
            }
            >
            Удалить
            </button>
            </>
          )}
          </div>
          <div>
          <label>Файл подписи:</label>
          {!uploadedSig ? (
            <>
            <input
            type="file"
            accept=".sig"
            onChange={handleSigChange}
            />
            </>
          ) : (
            <>
            {uploadedSig.file_url && (
              <button onClick={() => handleDownload(uploadedSig.file_url, "signature.sig", pdfInfo.token)}>
              Скачать файл подписи
              </button>
            )
            }

            <button
            onClick={() =>
              handleRemoveUploadedFile(logId, "sig")
            }
            disabled={
              logDetails[logId].log_status !== "in_progress" &&
              logDetails[logId].log_status !== "is_done"
            }
            >
            Удалить
            </button>
            </>
          )}
          </div>
          <button
          onClick={() =>
            handleFileUpload(logId, logDetails[logId].log_status)
          }
          >
          Загрузить файлы
          </button>
          </div>
          </div>
        ) : null
      )}
      </div>
    )}
    </div>
  );
};

export default AccountingPost;
